parameters.jsCode:
// Message Processing and Quote Handling System
const webhook = $input.first();
const userId = webhook.json.body.events[0].source.userId;
const messageText = webhook.json.body.events[0].message.text;
const messageId = webhook.json.body.events[0].message.id;
const replyToken = webhook.json.body.events[0].replyToken;
const timestamp = webhook.json.body.events[0].timestamp;
const quoteToken = webhook.json.body.events[0].message.quoteToken || null;
const quotedMessageId = webhook.json.body.events[0].message.quotedMessageId || null;

// Create message object
const messageObj = {
  userId,
  messageId,
  messageText,
  replyToken,
  timestamp,
  quoteToken,
  quotedMessageId,
  processed: false,
  createdAt: new Date().toISOString(),
  // Check if this is a quoted message
  isQuotedMessage: !!quotedMessageId,
  // Format message for processing
  processedText: quotedMessageId ? 
    `[Replying to message ID: ${quotedMessageId}] ${messageText}` : 
    messageText,
  // Quote configuration - set to true if you want to quote every user message
  shouldQuoteInResponse: true  // Change to false if you don't want quoting
};

return [{
  json: messageObj
}];

parameters.text:
=You are a helpful AI assistant. Please respond to the following message in the user's language.

{% if $('Message Processor').item.json.isQuotedMessage %}
Note: This message is a reply to a previous message (ID: {{ $('Message Processor').item.json.quotedMessageId }}). Please consider this context when responding.
{% endif %}

User message: {{ $('Message Processor').item.json.processedText }}

Please provide a clear, helpful response in plain text without HTML formatting. Keep your response concise and under 1000 characters when possible, as this is for a mobile chat interface.

parameters.jsCode:
const messageProcessor = $("Message Processor").item;
const aiAgent = $("AI Language Model").item;

if (!messageProcessor?.json?.replyToken || !aiAgent?.json?.output) {
  throw new Error("Missing necessary data, please check the output structure of the Message Processor & AI Agent nodes.");
}

// Function to strip markdown formatting
const stripMarkdown = (text) => {
  if (!text) return '';
  return text
     .replace(/\*\*([^*]+)\*\*/g, '$1') // Bold **text**
     .replace(/__([^_]+)__/g, '$1')   // Bold __text__
     .replace(/\*([^*]+)\*/g, '$1')      // Italic *text*
     .replace(/_([^_]+)_/g, '$1')      // Italic _text_
     .replace(/`([^`]+)`/g, '$1')      // Inline code `code`
     .replace(/^\s*[-*+]\s+/gm, '')  // List items
     .replace(/^\s*\d+\.\s+/gm, ''); // Numbered list items
};

// Function to truncate text to LINE's limit
const truncateText = (text, maxLength = 4900) => {
  if (!text) return '';
  if (text.length <= maxLength) return text;

  // Try to truncate at a sentence boundary
  const truncated = text.substring(0, maxLength);
  const lastSentence = truncated.lastIndexOf('.');
  const lastQuestion = truncated.lastIndexOf('?');
  const lastExclamation = truncated.lastIndexOf('!');

  const lastPunctuation = Math.max(lastSentence, lastQuestion, lastExclamation);

  if (lastPunctuation > maxLength * 0.8) {
    // If we found punctuation in the last 20%, use it
    return truncated.substring(0, lastPunctuation + 1);
  } else {
    // Otherwise, truncate at word boundary
    const lastSpace = truncated.lastIndexOf(' ');
    if (lastSpace > maxLength * 0.8) {
      return truncated.substring(0, lastSpace) + '...';
    } else {
      return truncated.substring(0, maxLength - 3) + '...';
    }
  }
};

const replyToken = messageProcessor.json.replyToken;
const rawText = aiAgent.json.output;
const cleanedText = stripMarkdown(rawText).trim();
const finalText = truncateText(cleanedText);

// Validate text length (LINE limit is 5000 characters)
if (finalText.length > 5000) {
  throw new Error(`Message text too long: ${finalText.length} characters (max 5000)`);
}

if (finalText.length === 0) {
  throw new Error('Message text is empty after processing');
}

// Prepare the message object for LINE API
const lineMessage = {
  type: "text",
  text: finalText
};

// Add quote token to quote the user's original message in the response
if (messageProcessor.json.shouldQuoteInResponse && messageProcessor.json.quoteToken) {
  lineMessage.quoteToken = messageProcessor.json.quoteToken;
}

return [
  {
    json: {
      replyToken,
      text: finalText,
      originalText: cleanedText,
      textLength: finalText.length,
      wasTruncated: finalText.length < cleanedText.length,
      originalMessageId: messageProcessor.json.messageId,
      userId: messageProcessor.json.userId,
      isQuotedResponse: messageProcessor.json.isQuotedMessage,
      message: lineMessage
    },
  },
];
